INSERT INTO `mysite::SITE_ID::_page` VALUES (1,'Page',NULL,'{\"en\":\"Home\"}','index.html','{\"en\":\"Home\"}','{\"en\":\"\"}',NULL,'{\"en\":\"<h5>Welcome<\\/h5>\"}',NULL,::USER_ID::,1724649766,::TIMESTAMP::,1,0,'0',NULL,1,0,'',0),(2,'Product','Shipping','{\"en\":\"iPhone 15\"}','iphone-15','{\"en\":\"iPhone 15, iPhone 15 Pro 128GB, 256GB, 512GB Black, Blue, Green, Yellow, Pink\"}','{\"en\":\"iPhone 15, iPhone 15 Pro 128GB, 256GB, 512GB Black, Blue, Green, Yellow, Pink\"}',NULL,'{\"en\":\"<p>The iPhone 15 offers cutting-edge technology with the powerful A16 Bionic chip, delivering faster performance and efficiency. Its stunning Super Retina XDR display provides vibrant visuals, while the upgraded 48MP camera system captures incredible detail and clarity, even in low light. The iPhone 15 introduces USB-C charging for faster and more versatile connectivity, along with enhanced battery life to keep you going all day. Available in a range of stylish colors, the iPhone 15 also features Dynamic Island and satellite emergency SOS, making it the perfect blend of innovation and practicality.<\\/p><h5 style=\\\"text-align:center;\\\"><b>This is a sample product. Unpublish or Delete this product as you like<\\/b><\\/h5>\"}','https://cdn.pageee.com/public/uploads/site/5281/2024Q3/iphone-15.webp',::USER_ID::,1724563192,::TIMESTAMP::,1,0,'0',NULL,1,1,'',0),(4,'Link',NULL,'{\"en\":\"Store\"}','/store',NULL,NULL,NULL,NULL,NULL,::USER_ID::,1724649861,1724649861,1,NULL,NULL,NULL,1,NULL,NULL,0),(5,'Page',NULL,'{\"en\":\"Pricing\"}','pricing','{\"en\":\"Pricing Landing Page\"}','{\"en\":\"This landing page contains all about our pricing.\"}',NULL,'{\"wysiwyg\":\"1\",\"en\":\" <div class=\\\"row\\\"><div class=\\\"sg-block-wrapper col-12 position-absolute top-0 start-0 z-1 pt-2\\\" data-sg-func=\\\"system__.bootstrap5.button\\\">              <div class=\\\"sg-block-content text-end\\\" style=\\\"\\\"><a class=\\\"m-1 sg-editor-template sg-editor-removable text-secondary text-decoratio